<script type="text/javascript">
var URL = 'ajax/wptab.html';
function sendRequest(url, data, type) {
    var type = type || 'POST';
    return $.ajax({
        type: type,
        data: data,
        dataType: 'json',
        url: url
    })
    .fail(function(a,b){
        console.log('Fail..', a, b);
    }) 
};

function populateData(obj) {
    return {
        type: $(obj).data('type'),
        cid: $(obj).data('cid')
    };
};

function Parser(name) {
    var self = this;
    self.name = name;
    self.all = $('#'+name+'-target').html();
    self.data = {};
    self.showAll = function(e) {
        $('#'+name+'-target').html(self.all);
    };
    self.click = function(obj) {
        var data = populateData(obj), 
            callback = function(d) {
                if(d.success) {
                    var html = $('#'+name+'-tpl').html(),
                        tpl = Handlebars.compile(html),
                        str = tpl(d)    
                    ;
                    $('#'+name+'-target').html(str);
                }
            }
        ;

        if(self.data[data.cid]) {
            callback.call(self, self.data[data.cid]);
        }
        else {
            sendRequest(URL, data, 'GET')
                .done(function(d){
                    self.data[d.cid] = d;
                    callback.call(self, d);
                })
        }
    }
}

var Jelajah = new Parser('jelajah'),
    Directory = new Parser('directory'),
    Hukum = new Parser('hukum')
;
</script>

